import React, { useState, useEffect, useRef, useCallback } from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import Marquee from './components/Marquee';
import AddCityForm from './components/AddCityForm';
import CityList from './components/CityList';
import Footer from './components/Footer';
import Notification from './components/Notification';
import { API } from './constant';

const DEFAULT_CITIES = ['Tokyo', 'New York', 'London'];

function App() {
  // Initialize cities from localStorage as a fallback.
  const [cities, setCities] = useState(() => {
    const stored = localStorage.getItem('cities');
    return stored ? JSON.parse(stored) : [];
  });
  const [notification, setNotification] = useState(null);
  const formRef = useRef(null);
  const debounceTimerRef = useRef(null);

  // Function to update localStorage.
  const updateLocalStorage = (newCities) => {
    localStorage.setItem('cities', JSON.stringify(newCities));
  };

  // syncCities fetches the current list from the backend.
  const syncCities = useCallback(() => {
    fetch(API + 'cities/', { credentials: 'include' })
      .then(response => response.json())
      .then(data => {
        setCities(data);
        updateLocalStorage(data);
        // Check if any default city is missing.
        const currentCityNames = data.map(city => city.name.toLowerCase());
        const missingDefaults = DEFAULT_CITIES.filter(
          def => !currentCityNames.includes(def.toLowerCase())
        );
        if (missingDefaults.length > 0) {
          // If defaults are missing, debounce a call to reset them.
          if (debounceTimerRef.current) clearTimeout(debounceTimerRef.current);
          debounceTimerRef.current = setTimeout(() => {
            resetDefaults();
          }, 20000); // 20 seconds
        }
      })
      .catch(err => console.error("Error syncing cities:", err));
  }, []);

  // resetDefaults calls the backend endpoint to restore default cities.
  const resetDefaults = useCallback(() => {
    fetch(API + 'reset-defaults/', {
      method: 'POST',
      credentials: 'include'
    })
      .then(response => response.json())
      .then(data => {
        showNotification({ type: 'success', message: data.message });
        syncCities(); // Refresh the list after restoring defaults.
      })
      .catch(err => {
        console.error("Error resetting defaults:", err);
        showNotification({ type: 'danger', message: "Error restoring default cities." });
      });
  }, [syncCities]);

  // Initial sync on mount.
  useEffect(() => {
    syncCities();
  }, [syncCities]);

  const showNotification = (notif) => {
    setNotification(notif);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // For both add and delete actions, sync immediately and then let the debounce check run.
  const addCity = () => {
    syncCities();
  };

  const removeCity = () => {
    syncCities();
  };

  return (
    <div>
      <Header />
      <Hero />
      <Marquee />
      <section
        className="section"
        style={{
          background: "url('/media/metofficegovuk_heroXLarge.jpeg') no-repeat center center",
          backgroundSize: 'cover'
        }}
      >
        <div className="container">
          <div className="columns">
            <div className="column is-offset-4 is-4" ref={formRef}>
              <AddCityForm
                cities={cities}
                onCityAdded={addCity}
                showNotification={showNotification}
              />
              {notification && (
                <Notification
                  type={notification.type}
                  message={notification.message}
                  onClose={() => setNotification(null)}
                />
              )}
            </div>
          </div>
        </div>
      </section>
      <section className="section">
        <div className="container">
          <div className="columns">
            <CityList
              cities={cities}
              onCityDeleted={removeCity}
              showNotification={showNotification}
            />
          </div>
        </div>
      </section>
      <hr style={{ borderTop: "2px solid #000", marginBottom: 0 }} />
      <Footer />
    </div>
  );
}

export default App;
