import React, { useState, useEffect, useRef } from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import Marquee from './components/Marquee';
import AddCityForm from './components/AddCityForm';
import CityList from './components/CityList';
import Footer from './components/Footer';
import Notification from './components/Notification';
import { API } from './constant';

function App() {
  // Initialize cities from localStorage as a fallback.
  const [cities, setCities] = useState(() => {
    const stored = localStorage.getItem('cities');
    return stored ? JSON.parse(stored) : [];
  });

  const [notification, setNotification] = useState(null);
  const formRef = useRef(null);

  // Function to update localStorage
  const updateLocalStorage = (newCities) => {
    localStorage.setItem('cities', JSON.stringify(newCities));
  };

  // Function to sync cities from the backend API.
  const syncCities = () => {
    fetch(API + 'cities/', { credentials: 'include' })
      .then(response => response.json())
      .then(data => {
        // Assuming data is an array of city objects.
        setCities(data);
        updateLocalStorage(data);
      })
      .catch(err => console.error("Error syncing cities:", err));
  };

  // Initial sync and periodic sync every 30 seconds.
  useEffect(() => {
    syncCities();
    const interval = setInterval(() => {
      syncCities();
    }, 30000); // 30000ms = 30 seconds

    return () => clearInterval(interval);
  }, []);

  const showNotification = (notif) => {
    setNotification(notif);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // After adding or deleting a city, re-sync the list.
  const addCity = (newCity) => {
    // Option 1: You could just push newCity into state
    // setCities(prev => [...prev, newCity]);
    // Option 2: To ensure complete synchronization, re-fetch:
    syncCities();
  };

  const removeCity = (cityId) => {
    // After deletion, re-fetch the list.
    syncCities();
  };

  // Call CSRF endpoint (if still needed)
  useEffect(() => {
    fetch('/set-csrf/', { credentials: 'include' })
      .then(response => {
        console.log("Set CSRF response status:", response.status);
      })
      .catch(err => console.error("Error setting CSRF cookie:", err));
  }, []);

  return (
    <div>
      <Header />
      <Hero />
      <Marquee />
      <section
        className="section"
        style={{
          background: "url('/media/metofficegovuk_heroXLarge.jpeg') no-repeat center center",
          backgroundSize: 'cover'
        }}
      >
        <div className="container">
          <div className="columns">
            <div className="column is-offset-4 is-4" ref={formRef}>
              <AddCityForm
                cities={cities}
                onCityAdded={addCity}
                showNotification={showNotification}
              />
              {notification && (
                <Notification
                  type={notification.type}
                  message={notification.message}
                  onClose={() => setNotification(null)}
                />
              )}
            </div>
          </div>
        </div>
      </section>
      <section className="section">
        <div className="container">
          <div className="columns">
            <CityList
              cities={cities}
              onCityDeleted={removeCity}
              showNotification={showNotification}
            />
          </div>
        </div>
      </section>
      <hr style={{ borderTop: "2px solid #000", marginBottom: 0 }} />
      <Footer />
    </div>
  );
}

export default App;
