import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import Marquee from './components/Marquee';
import AddCityForm from './components/AddCityForm';
import CityList from './components/CityList';
import Footer from './components/Footer';
import Notification from './components/Notification';

function App() {
  // Default cities.
  const defaultCities = [
    { id: 'tokyo', name: 'Tokyo' },
    { id: 'london', name: 'London' },
    { id: 'newyork', name: 'New York' }
  ];

  // Initializer: if localStorage exists but is empty, use defaults.
  const [cities, setCities] = useState(() => {
    const stored = localStorage.getItem('cities');
    if (stored) {
      const parsed = JSON.parse(stored);
      return parsed.length > 0 ? parsed : defaultCities;
    }
    return defaultCities;
  });

  // Persist changes.
  useEffect(() => {
    localStorage.setItem('cities', JSON.stringify(cities));
  }, [cities]);

  // Global notification state.
  const [notification, setNotification] = useState(null);
  const showNotification = (notif) => setNotification(notif);

  const addCity = (newCity) => {
    setCities(prev => [...prev, newCity]);
  };

  const removeCity = (cityId) => {
    setCities(prev => prev.filter(city => city.id !== cityId));
  };

  return (
    <div>
      <Header />
      <Hero />
      <Marquee />
      <section
        className="section"
        style={{
          background: "url('/media/metofficegovuk_heroXLarge.jpeg') no-repeat center center",
          backgroundSize: 'cover'
        }}
      >
        <div className="container">
          <div className="columns">
            <div className="column is-offset-4 is-4">
              <AddCityForm
                cities={cities}
                onCityAdded={addCity}
                showNotification={showNotification}
              />
            </div>
          </div>
        </div>
      </section>
      <section className="section">
        <div className="container">
          <div className="columns">
            <CityList
              cities={cities}
              onCityDeleted={removeCity}
              showNotification={showNotification}
            />
          </div>
        </div>
      </section>
      <hr style={{ borderTop: "2px solid #000", marginBottom: 0 }} />
      <Footer />
      {notification && (
        <Notification
          type={notification.type}
          message={notification.message}
          onClose={() => setNotification(null)}
        />
      )}
    </div>
  );
}

export default App;
