import React from "react";
import { useParams, Link } from "react-router-dom";
import { Container, ListGroup } from "react-bootstrap";
import LoadingSpinner from "../components/LoadingSpinner";
import ErrorAlert from "../components/ErrorAlert";
import Breadcrumbs from "../components/Breadcrumbs";
import { useQuery } from "@tanstack/react-query";

/**
 * Example constants for the weather API.
 * Replace these with your own or move them into a dedicated `constants.js`.
 */
const WEATHER_API_BASE = "https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/";
const WEATHER_API_KEY = "YOUR_API_KEY_HERE"; // e.g., 'BP8GPRYV53UQ56SHJR5EFCJ77'

function WeatherPage() {
  // Get the city name from the URL: e.g., /weather/:city
  const { city } = useParams();

  // Query to fetch weather data for the given city
  const {
    data: weatherData,
    isLoading,
    isError,
    error,
  } = useQuery({
    queryKey: ["weather", city],
    queryFn: async () => {
      // Build the full API URL
      const url = `${WEATHER_API_BASE}${encodeURIComponent(city)}?unitGroup=metric&key=${WEATHER_API_KEY}&contentType=json`;
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error(`Failed to fetch weather data for '${city}'`);
      }
      return response.json(); // The API typically returns an object with currentConditions + days
    },
  });

  // Show a loading spinner while data is being fetched
  if (isLoading) {
    return <LoadingSpinner />;
  }

  // Show an error alert if the API request fails
  if (isError) {
    return <ErrorAlert message={error?.message} />;
  }

  /**
   * Example structure of `weatherData` from Visual Crossing:
   * {
   *   "resolvedAddress": "London, England, United Kingdom",
   *   "latitude": 51.5085,
   *   "longitude": -0.1257,
   *   "timezone": "Europe/London",
   *   "currentConditions": {
   *       "temp": 14.3,
   *       "conditions": "Partly cloudy",
   *       ...
   *   },
   *   "days": [
   *       {
   *         "datetime": "2025-03-30",
   *         "temp": 15.2,
   *         "conditions": "Rain, Overcast",
   *         ...
   *       },
   *       ...
   *   ]
   * }
   */

  // Prepare breadcrumb links
  // Feel free to adjust or remove if you donâ€™t need breadcrumbs.
  const breadcrumbLinks = [
    { label: "Home", to: "/" },
    { label: "Weather", to: "/weather" },
    { label: city, to: `/weather/${city}` },
  ];

  // Extract current conditions and forecast days
  const { currentConditions, days = [] } = weatherData || {};
  const { temp, conditions } = currentConditions || {};

  return (
    <Container>
      {/* Breadcrumb navigation (optional) */}
      <Breadcrumbs links={breadcrumbLinks} />

      {/* Page heading */}
      <h1>Weather in {city}</h1>

      {/* Current weather conditions */}
      {currentConditions ? (
        <div style={{ marginBottom: "20px" }}>
          <h2>Current Conditions</h2>
          <p>
            <strong>Temperature:</strong> {temp}Â°C
          </p>
          <p>
            <strong>Conditions:</strong> {conditions}
          </p>
        </div>
      ) : (
        <p>No current weather data available.</p>
      )}

      {/* Forecast (days array) */}
      <h2>Forecast</h2>
      {days.length > 0 ? (
        <ListGroup>
          {days.map((day) => (
            <ListGroup.Item key={day.datetime}>
              <strong>{day.datetime}:</strong> {day.conditions},{" "}
              {day.temp}Â°C
            </ListGroup.Item>
          ))}
        </ListGroup>
      ) : (
        <p>No forecast data available.</p>
      )}

      {/* Optional link back somewhere else */}
      <div style={{ marginTop: "20px" }}>
        <Link to="/">Back to Home</Link>
      </div>
    </Container>
  );
}

export default WeatherPage;
