import React, { useState, useEffect, useRef, useCallback } from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import Marquee from './components/Marquee';
import AddCityForm from './components/AddCityForm';
import CityList from './components/CityList';
import Footer from './components/Footer';
import Notification from './components/Notification';
import { API } from './constant';

function App() {
  const [cities, setCities] = useState(() => {
    const stored = localStorage.getItem('cities');
    return stored ? JSON.parse(stored) : [];
  });
  const [notification, setNotification] = useState(null);
  const formRef = useRef(null);

  const updateLocalStorage = (newCities) => {
    localStorage.setItem('cities', JSON.stringify(newCities));
  };

  const syncCities = useCallback(() => {
    fetch(API + 'cities/', { credentials: 'include' })
      .then(response => response.json())
      .then(data => {
        setCities(data);
        updateLocalStorage(data);
      })
      .catch(err => console.error("Error syncing cities:", err));
  }, []);

  useEffect(() => {
    syncCities();
  }, [syncCities]);

  const showNotification = (notif) => {
    setNotification(notif);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const addCity = () => {
    syncCities();
  };

  const removeCity = () => {
    syncCities();
  };

  // Manual handler to reset default cities.
  const resetDefaults = () => {
    fetch(API + 'reset-defaults/', {
      method: 'POST',
      credentials: 'include'
    })
      .then(response => response.json())
      .then(data => {
        showNotification({ type: 'success', message: data.message });
        syncCities();
      })
      .catch(err => {
        console.error("Error resetting defaults:", err);
        showNotification({ type: 'danger', message: "Error resetting default cities." });
      });
  };

  useEffect(() => {
    fetch('/set-csrf/', { credentials: 'include' })
      .then(response => console.log("Set CSRF response status:", response.status))
      .catch(err => console.error("Error setting CSRF cookie:", err));
  }, []);

  return (
    <div>
      <Header />
      <Hero />
      <Marquee />
      <section
        className="section"
        style={{
          background: "url('/media/metofficegovuk_heroXLarge.jpeg') no-repeat center center",
          backgroundSize: 'cover'
        }}
      >
        <div className="container">
          <div className="columns">
            <div className="column is-offset-4 is-4" ref={formRef}>
              <AddCityForm
                cities={cities}
                onCityAdded={addCity}
                showNotification={showNotification}
              />
              {notification && (
                <Notification
                  type={notification.type}
                  message={notification.message}
                  onClose={() => setNotification(null)}
                />
              )}
              {/* Manual Reset Defaults Button */}
              <button
                className="button is-warning"
                onClick={resetDefaults}
                style={{ marginTop: '20px' }}
              >
                Reset Default Cities
              </button>
            </div>
          </div>
        </div>
      </section>
      <section className="section">
        <div className="container">
          <div className="columns">
            <CityList
              cities={cities}
              onCityDeleted={removeCity}
              showNotification={showNotification}
            />
          </div>
        </div>
      </section>
      <hr style={{ borderTop: "2px solid #000", marginBottom: 0 }} />
      <Footer />
    </div>
  );
}

export default App;
