import React, { useState, useEffect, useRef } from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import Marquee from './components/Marquee';
import AddCityForm from './components/AddCityForm';
import CityList from './components/CityList';
import Footer from './components/Footer';
import Notification from './components/Notification';

function App() {
  // Default cities that must always be present on refresh.
  const defaultCities = [
    { id: 'tokyo', name: 'Tokyo' },
    { id: 'london', name: 'London' },
    { id: 'newyork', name: 'New York' },
    { id: 'paris', name: 'Paris'}
  ];

  // Initialize cities from localStorage. If stored list is empty, add default cities.
  const [cities, setCities] = useState(() => {
    const stored = localStorage.getItem('cities');
    let initialCities = stored ? JSON.parse(stored) : [];
    defaultCities.forEach(defaultCity => {
      if (!initialCities.some(city => city.id.toLowerCase() === defaultCity.id)) {
        initialCities.push(defaultCity);
      }
    });
    return initialCities;
  });

  // Persist changes.
  useEffect(() => {
    localStorage.setItem('cities', JSON.stringify(cities));
  }, [cities]);

  // Global notification state.
  const [notification, setNotification] = useState(null);
  // Ref for the add form container.
  const formRef = useRef(null);

  // Global function to show notification and scroll to top.
  const showNotification = (notif) => {
    setNotification(notif);
    // Scroll all the way to the top of the page.
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const addCity = (newCity) => {
    setCities(prev => [...prev, newCity]);
  };

  // Remove city from state.
  const removeCity = (cityId) => {
    setCities(prev => prev.filter(city => city.id !== cityId));
  };

  return (
    <div>
      <Header />
      <Hero />
      <Marquee />
      {/* Add City Form Section with background image and proper column formatting */}
      <section
        className="section"
        style={{
          background: "url('/media/metofficegovuk_heroXLarge.jpeg') no-repeat center center",
          backgroundSize: 'cover'
        }}
      >
        <div className="container">
          <div className="columns">
            <div className="column is-offset-4 is-4" ref={formRef}>
              <AddCityForm
                cities={cities}
                onCityAdded={addCity}
                showNotification={showNotification}
              />
              {/* Notification rendered directly under the add form */}
              {notification && (
                <Notification
                  type={notification.type}
                  message={notification.message}
                  onClose={() => setNotification(null)}
                />
              )}
            </div>
          </div>
        </div>
      </section>
      {/* City List Section */}
      <section className="section">
        <div className="container">
          <div className="columns">
            <CityList
              cities={cities}
              onCityDeleted={removeCity}
              showNotification={showNotification}
            />
          </div>
        </div>
      </section>
      <hr style={{ borderTop: "2px solid #000", marginBottom: 0 }} />
      <Footer />
    </div>
  );
}

export default App;
