import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import Marquee from './components/Marquee';
import AddCityForm from './components/AddCityForm';
import CityList from './components/CityList';
import Footer from './components/Footer';

function App() {
  // Default cities to use if localStorage is empty.
  const defaultCities = [
    { id: 'tokyo', name: 'Tokyo' },
    { id: 'london', name: 'London' },
    { id: 'newyork', name: 'New York' }
  ];

  // Initialize state from localStorage, otherwise use defaultCities.
  const [cities, setCities] = useState(() => {
    const storedCities = localStorage.getItem('cities');
    return storedCities ? JSON.parse(storedCities) : defaultCities;
  });

  // Update localStorage whenever cities change.
  useEffect(() => {
    localStorage.setItem('cities', JSON.stringify(cities));
  }, [cities]);

  const addCity = (newCity) => {
    setCities(prev => [...prev, newCity]);
  };

  const removeCity = (cityId) => {
    setCities(prev => prev.filter(city => city.id !== cityId));
  };

  return (
    <div>
      <Header />
      <Hero />
      <Marquee />
      <section className="section">
        <div className="container">
          <AddCityForm cities={cities} onCityAdded={addCity} />
          <CityList cities={cities} setCities={setCities} onCityDeleted={removeCity} />
        </div>
      </section>
      <Footer />
    </div>
  );
}

export default App;
