import React, { useState, useEffect, useCallback } from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import Marquee from './components/Marquee';
import AddCityForm from './components/AddCityForm';
import CityList from './components/CityList';
import Footer from './components/Footer';
import Notification from './components/Notification';
import { API } from './constant';

function App() {
  const [cities, setCities] = useState(() => {
    const stored = localStorage.getItem('cities');
    return stored ? JSON.parse(stored) : [];
  });
  const [notification, setNotification] = useState(null);

  // This function syncs the cities from the backend.
  const syncCities = useCallback(() => {
    fetch(API + 'cities/', { credentials: 'include' })
      .then(response => {
        if (!response.ok) {
          throw new Error(`Failed to fetch cities: ${response.statusText}`);
        }
        return response.json();
      })
      .then(data => {
        setCities(data);
        localStorage.setItem('cities', JSON.stringify(data));
      })
      .catch(err => {
        console.error("Error syncing cities:", err);
        setNotification({ type: 'danger', message: 'Error syncing cities' });
      });
  }, []);

  // Sync cities when the app loads.
  useEffect(() => {
    syncCities();
  }, [syncCities]);

  const showNotification = (notif) => {
    setNotification(notif);
    // Scroll to the top so the user sees the notification.
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // After adding or deleting a city, re-sync the list.
  const addCity = (newCity) => {
    syncCities();
  };

  const removeCity = (cityId) => {
    syncCities();
  };

  return (
    <div>
      <Header />
      <Hero />
      <Marquee />
      <section
        className="section"
        style={{
          background: "url('/media/metofficegovuk_heroXLarge.jpeg') no-repeat center center",
          backgroundSize: 'cover'
        }}
      >
        <div className="container">
          <div className="columns">
            <div className="column is-offset-4 is-4">
              <AddCityForm
                cities={cities}
                onCityAdded={addCity}
                showNotification={showNotification}
              />
              {notification && (
                <Notification
                  type={notification.type}
                  message={notification.message}
                  onClose={() => setNotification(null)}
                />
              )}
            </div>
          </div>
        </div>
      </section>
      <section className="section">
        <div className="container">
          <div className="columns">
            <CityList
              cities={cities}
              onCityDeleted={removeCity}
              showNotification={showNotification}
            />
          </div>
        </div>
      </section>
      <hr style={{ borderTop: "2px solid #000", marginBottom: 0 }} />
      <Footer />
    </div>
  );
}

export default App;
