import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import Marquee from './components/Marquee';
import AddCityForm from './components/AddCityForm';
import CityList from './components/CityList';
import Footer from './components/Footer';

function App() {
  // Default cities if no saved data exists.
  const defaultCities = [
    { id: 'tokyo', name: 'Tokyo' },
    { id: 'london', name: 'London' },
    { id: 'newyork', name: 'New York' }
  ];

  // Load from localStorage or use defaults.
  const [cities, setCities] = useState(() => {
    const stored = localStorage.getItem('cities');
    return stored ? JSON.parse(stored) : defaultCities;
  });

  // Persist changes.
  useEffect(() => {
    localStorage.setItem('cities', JSON.stringify(cities));
  }, [cities]);

  const addCity = (newCity) => {
    setCities(prev => [...prev, newCity]);
  };

  const removeCity = (cityId) => {
    setCities(prev => prev.filter(city => city.id !== cityId));
  };

  return (
    <div>
      <Header />
      <Hero />
      <Marquee />
      {/* Add Form Section with background image */}
      <section
        className="section"
        style={{
          background: "url('/media/metofficegovuk_heroXLarge.jpeg') no-repeat center center",
          backgroundSize: 'cover'
        }}
      >
        <div className="container">
          <div className="columns">
            <div className="column is-offset-4 is-4">
              <AddCityForm cities={cities} onCityAdded={addCity} />
            </div>
          </div>
        </div>
      </section>
      {/* City List Section */}
      <section className="section">
        <div className="container">
          <div className="columns">
            <CityList cities={cities} onCityDeleted={removeCity} />
          </div>
        </div>
      </section>
      {/* Horizontal rule above footer */}
      <hr style={{ borderTop: "2px solid #000", marginBottom: 0 }} />
      <Footer />
    </div>
  );
}

export default App;
