import React, { useState } from 'react';

const AddCityForm = ({ cities, onCityAdded, showNotification }) => {
  const [cityName, setCityName] = useState('');

  // Helper: Get CSRF token from cookies.
  const getCSRFToken = () => {
    const match = document.cookie.match(new RegExp('(^| )csrftoken=([^;]+)'));
    return match ? match[2] : '';
  };

  // Validate city via Visual Crossing API.
  // Accepts the city if either currentConditions exists OR if data.days exists with length > 0.
  const validateCity = (name) => {
    const apikey = 'BP8GPRYV53UQ56SHJR5EFCJ77';
    const apiUrl = `https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/${encodeURIComponent(name)}?unitGroup=metric&key=${apikey}&contentType=json`;
    return fetch(apiUrl)
      .then(response => {
        if (!response.ok) throw new Error("Invalid city");
        return response.json();
      })
      .then(data => {
        console.log("Validation data for", name, data);
        if (data && (data.currentConditions || (data.days && data.days.length > 0))) {
          return true;
        } else {
          throw new Error("Invalid city");
        }
      });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const trimmedName = cityName.trim();
    if (!trimmedName) return;

    // Duplicate check.
    if (cities.some(city => city.name.toLowerCase() === trimmedName.toLowerCase())) {
      showNotification({ type: 'danger', message: `City '${trimmedName}' already exists in the list!` });
      return;
    }

    // Validate the city.
    validateCity(trimmedName)
      .then(() => {
        return fetch('/ajax/cities/add/', {
          method: 'POST',
          credentials: 'include',
          headers: {
            'Content-Type': 'application/json',
            'X-CSRFToken': getCSRFToken()
          },
          body: JSON.stringify({ name: trimmedName })
        });
      })
      .then(res => res.json())
      .then(data => {
        if (data.status === 'success') {
          showNotification({ type: 'success', message: "City added successfully!" });
          onCityAdded(data.city);
          setCityName('');
        } else {
          showNotification({ type: 'danger', message: `City '${trimmedName}' already exists in the list!` });
        }
      })
      .catch(err => {
        console.error('Validation or network error:', err);
        showNotification({ type: 'danger', message: `City '${trimmedName}' does not exist in the world!` });
      });
  };

  return (
    <div>
      <form id="addCityForm" onSubmit={handleSubmit}>
        <div className="field has-addons">
          <div className="control is-expanded">
            <input
              className="input"
              name="name"
              type="text"
              placeholder="City Name"
              required
              value={cityName}
              onChange={e => setCityName(e.target.value)}
            />
          </div>
          <div className="control">
            <button className="button is-info" type="submit">Add City</button>
          </div>
        </div>
      </form>
    </div>
  );
};

export default AddCityForm;
